function App() {

    const [swi, setSwi] = useState({ status:"login"});



    switch (swi.status) {
        case "login":
            return (
                <div>
                    <Login setSwi={setSwi} />
                </div>
            );

        case "goodLoginU":
            return (
                <div>
                    <UserMenu user={swi.user} />
                </div>
            );
